import { useMemo, useState } from 'react';
import {
  MaterialReactTable,
  useMaterialReactTable,
} from 'material-react-table';
import SuperAdminSidebar from '../../Components/SuperAdminSidebar';
import { useEffect } from 'react';

const AllEmployeesSuperAdmin = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [data, setData] = useState([]);
  const [count, setCount] = useState([]);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const fetchData = async () => {
    try {
      const res = await fetch(`${process.env.REACT_APP_BACKEND_URL}/allemp`);
      if (!res.ok) {
        throw new Error(`Failed to fetch data. Status: ${res}`);
      }

      const response = await res.json();
      console.log("data>>", response.data);
      setData(response.data);
      setCount(response.count)
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  //should be memoized or stable
  const columns = useMemo(
    () => [
      {
        accessorKey: 'emp_id',
        header: 'Employee ID',
        size: 50,
      },
      {
        accessorKey: 'emp_name',
        header: 'Employee Name',
        size: 200,
      },
      {
        accessorKey: 'em_loginid',
        header: 'Login Id',
        size: 150,
      },
      {
        accessorKey: 'contact',
        header: 'Contact',
        size: 200,
      },
      {
        accessorKey: 'cnic',
        header: 'CNIC',
        size: 200,
      },
      {
        accessorKey: 'designation',
        header: 'Desigination',
        size: 150,
      },
      {
        accessorKey: 'role',
        header: 'Role',
        size: 100,
      },
      {
        accessorKey: 'status',
        header: 'Status',
        size: 150,
      },
    ],
    []
  );

  const table = useMaterialReactTable({
    columns,
    enableColumnActions: false,
    enableColumnFilters: false,
    enableSorting: false,
    initialState: { density: 'compact' },
    data, 
    muiTableHeadCellProps: {
      sx: {
        fontWeight: 'bold',
        fontSize: '12px',
        border: '1px solid #e0e0e0',
        color: "black"
      },
    },
    muiTableBodyProps: {
      sx: {
        fontSize: "10px"
      }
    },
    muiTableBodyCellProps: {
      sx: {
        fontSize: "13px",
        borderRight: '2px solid #e0e0e0', //add a border between columns
      },
    },
    muiSkeletonProps: {
      animation: 'wave',
    },
    muiLinearProgressProps: {
      color: 'secondary',
    },
  });

  useEffect(() => {
    fetchData()
  }, [])


  return (
    <div className='flex h-[100vh] bg-black'>
      {isSidebarOpen && (
        <div className="sidebar"><SuperAdminSidebar /></div>
      )}
      <div className='rounded-xl m-2 p-2 w-100 overflow-y-scroll' style={{backgroundColor:"#F0EFEF"}}>
        <button onClick={toggleSidebar} className='bg-black'><img src='data:image/png;base64,' className='h-8 w-8 bg-black' /></button>
        <h1 className='text-4xl font-bold text-uppercase text-black py-3 my-3'>All Employes</h1>
        <h5 className='text-2xl font-bold text-uppercase text-black py-3 my-3'>Total Records : {count}</h5>
        <MaterialReactTable table={table} />
      </div>
    </div>)
};

export default AllEmployeesSuperAdmin;
