import { useEffect, useMemo, useState } from 'react';
import {
  MaterialReactTable,
  useMaterialReactTable,
} from 'material-react-table';
import { Link, useNavigate } from 'react-router-dom';
import Technical_Sidebar from '../../Components/Technical_Sidebar';


const DataLogTech = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };
  //should be memoized or stable
  const columns = useMemo(
    () => [
      {
        accessorKey: 'customer_name', //access nested data with dot notation
        header: 'Customer Name',
        size: 100,
      },
      {
        accessorKey: 'contact_person',
        header: 'Contact Person',
        size: 100,
      },
      {
        accessorKey: 'reg_no', //normal accessorKey
        header: 'Registration',
        size: 80,
      },
      {
        accessorKey: 'nature',
        header: 'Alert',
        size: 100,
      },
      {
        accessorKey: 'contact_no',
        header: 'Contact',
        size: 100,
      },
      {
        accessorKey: 'date',
        header: 'Day/Date',
        size: 80,
      },
      {
        accessorKey: 'time',
        header: 'Time',
        size: 80,
      },
      {
        accessorKey: 'remarks',
        header: 'Remarks',
        size: 100,
      },
    ],
    [],
  );

  const [data, setData] = useState([]);
  const [count,setCount ]=useState();  
  const navigate = useNavigate();

  const fetchData = async () => {
      try {
          const res = await fetch("http://127.0.0.1:8000/api/alldatalogs");
          if (!res.ok) {
              throw new Error(`Failed to fetch data. Status: ${res}`);
          }
          console.log(res)
          const response = await res.json();
          console.log("data>>", response);
          setData(response.data);
          setCount(response.count);
      } catch (error) {
          console.error("Error fetching data:", error);
      }
  };

  const table = useMaterialReactTable({
    columns,
    data, //data must be memoized or stable (useState, useMemo, defined outside of this component, etc.)
    muiTableHeadCellProps: {
      //simple styling with the `sx` prop, works just like a style prop in this example
      sx: {
        fontWeight: 'bold',
        fontSize: '12px',
        backgroundColor:"#9CA3AF",
        color:"black"
      },
    },
    muiTableBodyProps:{
      sx:{
          fontSize:"8px"
      }
    },
    muiTableBodyCellProps: {
      sx: {
          fontSize:"11px",
        borderRight: '2px solid #e0e0e0', //add a border between columns
      },
  }
  });

  useEffect(() => {
    fetchData();
}, []);

  return (
    <div className='flex h-[100vh] bg-black'>
      {isSidebarOpen && (
        <div className="sidebar"><Technical_Sidebar /></div>
      )}
      <div className='bg-white rounded-xl m-2 p-2 w-100 overflow-y-scroll bg'>
        <button onClick={toggleSidebar} className='bg-black'><img src='data:image/png;base64,' className='h-8 w-8 bg-black' /></button>
       <div className='flex py-3 my-3 justify-between'> <h1 className='text-4xl font-bold text-uppercase text-black'> Data Log  </h1> </div>
        <MaterialReactTable table={table} />
      </div>
    </div>)
};

export default DataLogTech ;
