import { useEffect, useMemo, useState } from 'react';
import {
  MaterialReactTable,
  useMaterialReactTable,
} from 'material-react-table';
import { useNavigate } from 'react-router-dom';
import Technical_Sidebar from '../../Components/Technical_Sidebar';

const RedoTech = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const navigate = useNavigate();
  const [count, setCount] = useState();
  const [data, setData] = useState([])

  const fetchData = async () => {
    try {
      const res = await fetch(`${process.env.REACT_APP_BACKEND_URL}/all_redo_info`);
      if (!res.ok) {
        throw new Error(`Failed to fetch data. Status: ${res}`);
      }

      const response = await res.json();
      console.log("data>>", response.data);
      setData(response.data);
      setCount(response.total_count)
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const columns = useMemo(
    () => [
      {
        accessorKey: 'customer_name', //access nested data with dot notation
        header: 'Customer Name',
        size: 100,
      },
      {
        accessorKey: 'reg_no',
        header: 'Registration #',
        size: 100,
      },
      {
        accessorKey: 'old_device', //normal accessorKey
        header: 'Old Device',
        size: 100,
      },
      {
        accessorKey: 'new_device',
        header: 'New Device',
        size: 100,
      },
      {
        accessorKey: 'harness_change',
        header: 'Harness',
        size: 100,
      },
      {
        accessorKey: 'backupbattery_change',
        header: 'Backup Battery',
        size: 100,
      },
      {
        accessorKey: 'technician',
        header: 'Technicain',
        size: 100,
      },
      {
        accessorKey: 'charges',
        header: 'Charges',
        size: 100,
      },
      {
        accessorKey: 'remarks',
        header: 'Reason',
        size: 100,
      },
      {
        accessorKey: 'redo_date',
        header: 'Redo Date',
        size: 100,
      },
      {
        accessorKey: 'redo_time',
        header: 'Redo Time',
        size: 100,
      },
      {
        accessorKey: 'representative',
        header: 'Representative',
        size: 100,
      },
    ],
    [],
  );

  const table = useMaterialReactTable({
    columns,
    data,
    enableColumnActions: false,
    enableColumnFilters: false,
    enableSorting: false,
    initialState: { density: 'compact' }, //data must be memoized or stable (useState, useMemo, defined outside of this component, etc.)
    muiTableHeadCellProps: {
      //simple styling with the `sx` prop, works just like a style prop in this example
      sx: {
        fontWeight: 'bold',
        fontSize: '12px',
        backgroundColor: "#9CA3AF",
        color: "black"
      },
    },
    muiTableBodyRowProps: ({ row }) => ({
      // onClick: (event) => {
      //   navigate(`/superAdmin/vehiclesInfo/${row.id}`)
      //   console.info(event, row.id);
      // },
      // sx: {
      //   cursor: 'pointer', //you might want to change the cursor too when adding an onClick
      // },
    }),
    muiTableBodyProps: {
      sx: {
        fontSize: "8px"
      }
    },
    muiTableBodyCellProps: {
      sx: {
        fontSize: "11px",
        borderRight: '2px solid #e0e0e0', //add a border between columns
      },
    }
  });


  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className='flex h-[100vh] bg-black'>
      {isSidebarOpen && (
        <div className="sidebar"><Technical_Sidebar /></div>
      )}
      <div className=' rounded-xl m-2 p-2 w-100 overflow-y-scroll ' style={{ backgroundColor:"#F0F0F0"}}>
        <button onClick={toggleSidebar}><img src='data:image/png;base64,' className='h-8 w-8' /></button>
        <h1 className='text-4xl font-bold text-uppercase text-black py-3 mt-3'>Redo Information</h1>
        <div className=' my-3'> <MaterialReactTable table={table} /> </div>
      </div>
    </div>)
};

export default RedoTech;
